    def test_shorten_labels(self):
        name1 = 'nouille2h7f4994y297_)Q()#&*$NF&*(G#'
        name2 = 'dinde 67GF&*G$?'
        name3 = 'arbre_*$G*Ff5g5'

        spec1 = [[1, 2, 3, 4, 5, 6, 7]]
        spec2 = [[1, 3, 2, 5, 4, 7, 6]]
        spec3 = [[7, 6, 5, 4, 3, 2, 1]]

        data1 = spectrum.ArrayToSpectra([1, 2, 3, 4, 5, 6, 7], spec1, name1).spectra
        data1 = spectrum.Spectra(data1)
        data2 = spectrum.ArrayToSpectra([1, 2, 3, 4, 5, 6, 7], spec2, name2).spectra
        data2 = spectrum.Spectra(data2)
        data3 = spectrum.ArrayToSpectra([1, 2, 3, 4, 5, 6, 7], spec3, name3).spectra
        data3 = spectrum.Spectra(data3)

        data1.add(data2, data3)

        data1.shortenLabels()
        assert ['nouille', 'dinde', 'arbre'] == data1.labelList